mapper.select:
select count(*) from sa_server where 1=1

    AND server_name LIKE "%"#{serverName}"%"


    AND type = #{type}


    AND os LIKE "%"#{os}"%"


    AND user_name = #{userName}

select * from sa_server where 1=1

    AND server_name LIKE "%"#{serverName}"%"


    AND type = #{type}


    AND os LIKE "%"#{os}"%"


    AND user_name = #{userName}


    limit #{offset}, #{limit}
